import { useEffect, useCallback } from "react";

useEffect(() => {
  function getHistory() {
    return document.getElementById("history-value").innerText;
  }
  getHistory();
});

const getHistory = useCallback(async (num) => {
    document.getElementById("history-value").innerText = num;
 }, []);
 
 useEffect(() => {
     getHistory();
 }, [getHistory]);


const printHistory = useCallback(async (num) => {
    document.getElementById("history-value").innerText = num;
 }, []);
 
 useEffect(() => {
     printHistory();
 }, [printHistory]);


const getOutput = useCallback(async (num) => {
    return document.getElementById("output-values").innerText;
 }, []);
 
 useEffect(() => {
     getOutput();
 }, [getOutput]);


 const printOutput = useCallback(async (num) => {
    if (num === "") {
        document.getElementById("output-values").innerText = num;
      } else {
        document.getElementById("output-values").innerText =
          getFormattedNumber(num);
      }
 }, []);
 
 useEffect(() => {
     printOutput();
 }, [printOutput]);







